Bash(cat >> /Users/korhrd/Documents/GitHub/korhrdsocial/app/admin/admin.module.css << 'EOF'

/* 날짜 구분자 */
.dateSeparator {
  color: #9ca3af;
  font-size: 14px;
  padding: 0 2px;
  line-height: 34px;
}

/* 엑셀식 TH 필터 */
.thFilterable {
  position: relative;
  white-space: nowrap;
}

.thInner {
  display: inline-flex;
  align-items: center;
  gap: 3px;
}

.thFilterBtn {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  width: 18px;
  height: 16px;
  background: rgba\(255,255,255,0.15\);
  border: 1px solid rgba\(255,255,255,0.3\);
  border-radius: 3px;
  color: rgba\(255,255,255,0.7\);
  font-size: 10px;
  cursor: pointer;
  padding: 0;
  line-height: 1;
  transition: all 0.15s;
  flex-shrink: 0;
}

.thFilterBtn:hover {
  background: rgba\(255,255,255,0.3\);
  color: #fff;
}

.thFilterBtnActive {
  background: #fbbf24 !important;
  border-color: #f59e0b !important;
  color: #92400e !important;
}

.thFilterDropdown {
  position: absolute;
  top: calc\(100% + 4px\);
  left: 0;
  z-index: 2000;
  background: #fff;
  border: 1px solid #e5e8eb;
  border-radius: 8px;
  box-shadow: 0 8px 24px rgba\(0,0,0,0.12\);
  min-width: 140px;
  overflow: hidden;
}

.thFilterSection {
  padding: 6px 0;
  border-bottom: 1px solid #f3f4f6;
}

.thFilterSection:last-child {
  border-bottom: none;
}

.thFilterSectionTitle {
  padding: 4px 12px 2px;
  font-size: 10px;
  font-weight: 700;
  color: #9ca3af;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.thFilterItem {
  padding: 7px 12px;
  font-size: 13px;
  color: #374151;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 8px;
  transition: background 0.1s;
  font-weight: 400;
}

.thFilterItem::before {
  content: '';
  width: 14px;
  height: 14px;
  border: 1.5px solid #d1d5db;
  border-radius: 3px;
  flex-shrink: 0;
  transition: all 0.1s;
}

.thFilterItem:hover {
  background: #f9fafb;
}

.thFilterItemSelected {
  color: #1d4ed8;
  font-weight: 600;
}

.thFilterItemSelected::before {
  background: #3182f6;
  border-color: #3182f6;
  background-image: url\("data:image/svg+xml,%3Csvg viewBox='0 0 10 8' fill='none' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M1 4l3 3 5-6' stroke='white' stroke-width='1.5' stroke-linecap='round' stroke-linejoin='round'/%3E%3C/svg%3E"\);
  background-size: 10px 8px;
  background-repeat: no-repeat;
  background-position: center;
}
EOF)
